// Funciones helper para el cuestionario
function saveQuestionnaireAnswer(participantId, questionId, answer) {
  const stmt = questionnaireDb.prepare(`
    INSERT INTO questionnaire_responses (participant_id, question_id, answer)
    VALUES (?, ?, ?)
    ON CONFLICT(participant_id, question_id) 
    DO UPDATE SET answer = ?, timestamp = CURRENT_TIMESTAMP
  `);
  
  const answerJson = JSON.stringify(answer);
  stmt.run(participantId, questionId, answerJson, answerJson);
}

function getQuestionnaireAnswers(participantId) {
  const stmt = questionnaireDb.prepare('SELECT question_id, answer FROM questionnaire_responses WHERE participant_id = ?');
  const rows = stmt.all(participantId);
  
  const answers = {};
  rows.forEach(row => {
    answers[row.question_id] = JSON.parse(row.answer);
  });
  
  return answers;
}

function updateQuestionnaireProgress(participantId, currentQuestion, completed = false) {
  const stmt = questionnaireDb.prepare(`
    INSERT INTO questionnaire_status (participant_id, current_question, completed, completed_at)
    VALUES (?, ?, ?, ?)
    ON CONFLICT(participant_id)
    DO UPDATE SET current_question = ?, completed = ?, completed_at = ?
  `);
  
  const completedAt = completed ? new Date().toISOString() : null;
  stmt.run(participantId, currentQuestion, completed ? 1 : 0, completedAt, currentQuestion, completed ? 1 : 0, completedAt);
}

function getQuestionnaireProgress(participantId) {
  const stmt = questionnaireDb.prepare('SELECT * FROM questionnaire_status WHERE participant_id = ?');
  return stmt.get(participantId);
}

