// ===== Helpers comunes =====
function csvEscape(s) {
  if (s == null) return '';
  const str = String(s);
  return /[",\n]/.test(str) ? `"${str.replace(/"/g, '""')}"` : str;
}

// üîß FUNCI√ìN CORREGIDA: normaliza g√©nero primero
function normalizeGender(val) {
  const v = String(val || '').trim();
  // Normalizar solo las variantes en ingl√©s a espa√±ol
  if (v.toLowerCase() === 'male' || v.toLowerCase() === 'm') return 'Masculino';
  if (v.toLowerCase() === 'female' || v.toLowerCase() === 'f') return 'Femenino';
  // Devolver tal cual si ya est√° en espa√±ol
  return v;
}

// üîß FUNCI√ìN CORREGIDA: recibe g√©nero YA normalizado ('f', 'm', o '')
function deriveTratamiento(normalizedGender) {
  if (normalizedGender === 'f') return 'Estimada';
  if (normalizedGender === 'm') return 'Estimado';
  return 'Estimad@';
}

function appendCsvRow(record) {
  try {
    const csvFile = path.join(DATA_DIR, 'responses.csv');
    const exists = fs.existsSync(csvFile) && fs.statSync(csvFile).size > 0;
    if (!exists) fs.writeFileSync(csvFile, 'participant_id,response,ts_utc,ip,user_agent\n');
    const row = [
      csvEscape(record.participant_id),
      csvEscape(record.response),
      csvEscape(record.ts_utc),
      csvEscape(record.ip),
      csvEscape(record.user_agent),
    ].join(',') + '\n';
    fs.appendFile(csvFile, row, (e) => e && console.error('CSV append error:', e));
  } catch (e) {
    console.error('CSV write error:', e);
  }
}

function findAsset(candidates) {
  for (const name of candidates) {
    const p = path.join(ASSETS_DIR, name);
    if (fs.existsSync(p)) return p;
  }
  return null;
}

