const BASE_URL = process.env.BASE_URL || 'http://localhost:3000';
const MAIL_LOG = path.join(DATA_DIR, 'mail_log.csv');

function getMailingList() {
  const map = loadParticipants();
  const list = [];
  for (const [id, p] of map.entries()) {
    if (!p.email) continue;
    list.push({
      id,
      name: p.name || p.full_name || id,
      full_name: p.full_name || p.name || id,
      email: p.email,
      lang: (p.lang || 'es').toLowerCase(),
      gender: p.gender || '',
      password: p.password || '',
      require_auth: p.require_auth || '',
      active: p.active || 'yes'
    });
  }
  return list;
}

// log de env√≠os (csv)
function appendMailLog({ id, email, subject, ts }) {
  try {
    const exists = fs.existsSync(MAIL_LOG) && fs.statSync(MAIL_LOG).size > 0;
    if (!exists) {
      fs.writeFileSync(MAIL_LOG, 'id,email,subject,ts_utc\n');
    }
    const line = [csvEscape(id), csvEscape(email), csvEscape(subject), csvEscape(ts)].join(',') + '\n';
    fs.appendFileSync(MAIL_LOG, line);
  } catch (e) {
    console.warn('[mailer] no se pudo registrar en mail_log.csv:', e);
  }
}

