app.get('/api/mailer/preview', (req, res) => {
  try {
    const i = Math.max(0, parseInt(req.query.i || '0', 10) || 0);
    const list = getMailingList();
    if (list.length === 0) {
      return res.status(404).json({ ok:false, error:'No hay destinatarios con email en el CSV' });
    }

    const idx = Math.min(i, list.length - 1);
    const rec = list[idx];

    // üîß CORRECCI√ìN: normalizar g√©nero y derivar tratamiento
    const genderNormalized = normalizeGender(rec.gender);
    const reqTrat = String(req.query.tratamiento || 'auto');

    let tratamiento;
    if (reqTrat === 'auto') {
      tratamiento = deriveTratamiento(genderNormalized);
    } else {
      // Usuario eligi√≥ manualmente, usar ese valor directamente
      tratamiento = reqTrat;
    }

    const subject = buildEmailSubject({ nombre: rec.name });
    const html    = buildEmailHTML({ tratamiento, nombre: rec.name, id: rec.id });

    res.json({
      ok: true,
      i: idx,
      total: list.length,
      toEmail: rec.email,
      toName: rec.name,
      id: rec.id,
      subject,
      html,
      tratamiento
    });
  } catch (e) {
    console.error('[mailer preview] error:', e);
    res.status(500).json({ ok:false, error:'Error generando vista previa' });
  }
});

// --- API: env√≠o ---
app.post('/api/mailer/send', async (req, res) => {
  try {
    const { i, tratamiento, subject: subjectOverride } = req.body || {};
    const idx = Math.max(0, parseInt(i || '0', 10) || 0);
    const list = getMailingList();
    if (list.length === 0) return res.status(404).json({ ok:false, error:'No hay destinatarios con email' });
    const rec = list[Math.min(idx, list.length - 1)];

    const subject = subjectOverride && String(subjectOverride).trim()
      ? String(subjectOverride).trim()
      : buildEmailSubject({ nombre: rec.name });

    const html = buildEmailHTML({
      tratamiento: String(tratamiento || 'Estimad@'),
      nombre: rec.name,
      id: rec.id
    });

    if (!EMAIL_USER || !EMAIL_PASS) {
      return res.status(400).json({ ok:false, error:'EMAIL_USER/EMAIL_PASS no configurados' });
    }
    if (!rec.email) {
      return res.status(400).json({ ok:false, error:'El destinatario no tiene email' });
    }

    await transporter.sendMail({
      from: `Prion Study System <${EMAIL_USER}>`,
      to: `${rec.name} <${rec.email}>`,
      subject,
      html
    });

    appendMailLog({ id: rec.id, email: rec.email, subject, ts: new Date().toISOString() });

    res.json({ ok:true, sentTo: rec.email, id: rec.id, subject });
  } catch (e) {
    console.error('[mailer send] error:', e);
    res.status(500).json({ ok:false, error:'No se pudo enviar el email' });
  }
});



