// --- ESQUEMA (crea tablas si no existen) ---
db.exec(`
CREATE TABLE IF NOT EXISTS questionnaire_answers (
  participant_id TEXT NOT NULL,
  question_id    TEXT NOT NULL,
  answer         TEXT NOT NULL,
  ts_utc         TEXT NOT NULL DEFAULT (datetime('now')),
  PRIMARY KEY (participant_id, question_id)
);

CREATE TABLE IF NOT EXISTS questionnaire_status (
  participant_id    TEXT PRIMARY KEY,
  current_question  TEXT,
  completed         INTEGER NOT NULL DEFAULT 0,
  completed_at      TEXT
);
`);

// --- PREPARED STATEMENTS ---
const qaUpsert = db.prepare(`
  INSERT INTO questionnaire_answers (participant_id, question_id, answer, ts_utc)
  VALUES (@participant_id, @question_id, @answer, @ts_utc)
  ON CONFLICT(participant_id, question_id) DO UPDATE SET
    answer = excluded.answer,
    ts_utc = excluded.ts_utc
`);

const qsGet     = db.prepare(`SELECT participant_id, current_question, completed, completed_at FROM questionnaire_status WHERE participant_id = ?`);
const qsInsert  = db.prepare(`INSERT INTO questionnaire_status (participant_id, current_question, completed, completed_at) VALUES (@participant_id, @current_question, @completed, @completed_at)`);
const qsUpdate  = db.prepare(`UPDATE questionnaire_status SET current_question = @current_question, completed = @completed, completed_at = @completed_at WHERE participant_id = @participant_id`);
const qaSelect  = db.prepare(`SELECT question_id, answer, ts_utc FROM questionnaire_answers WHERE participant_id = ? ORDER BY question_id`);

// --- HELPERS ---
function getAnswers(participantId) {
  // Devuelve objeto { [question_id]: { answer, ts_utc } }
  const rows = qaSelect.all(participantId);
  const map = {};
  for (const r of rows) {
    let processedAnswer = r.answer;
    
    // Deserializar objetos JSON para preguntas de ranking
    const rankingQuestions = ['P11', 'P20', 'P21', 'P22b'];
    if (rankingQuestions.includes(r.question_id) && typeof r.answer === 'string') {
      try {
        const parsed = JSON.parse(r.answer);
        if (typeof parsed === 'object' && parsed !== null) {
          processedAnswer = parsed;
        }
      } catch (e) {
        // Mantener como string si no es JSON v√°lido
      }
    }
    
    map[r.question_id] = { answer: processedAnswer, ts_utc: r.ts_utc };
  }
  return map;
}

function getQuestionnaireStatus(participantId) {
  const row = qsGet.get(participantId);
  if (!row) return null;
  return {
    participant_id: row.participant_id,
    current_question: row.current_question || null,
    completed: !!row.completed,
    completed_at: row.completed_at || null
  };
}

function saveAnswer(participantId, questionId, answer) {
  qaUpsert.run({
    participant_id: participantId,
    question_id: String(questionId),
    answer: (answer == null ? '' : (typeof answer === 'object' ? JSON.stringify(answer) : String(answer))),
    ts_utc: new Date().toISOString()
  });
}

function detectLanguageFromCSV(participantData) {
  const lang = participantData.language || 'es';
  const validLanguages = ['es', 'ca', 'eu', 'gl', 'va', 'en', 'pt', 'it', 'de', 'fr', 'sk', 'he'];
  
  if (validLanguages.includes(lang)) {
    return lang;
  }
  return 'es'; // Fallback a espa√±ol
}




function updateQuestionnaireStatus(participantId, currentQuestion = null, completed = false) {
  const existing = qsGet.get(participantId);
  const payload = {
    participant_id: participantId,
    current_question: currentQuestion,
    completed: completed ? 1 : 0,
    completed_at: completed ? new Date().toISOString() : (existing?.completed_at || null)
  };
  if (existing) qsUpdate.run(payload);
  else qsInsert.run(payload);
}


