app.get('/api/participant/:id/response-count', (req, res) => {
  try {
    const { id } = req.params;
    
    const responsesFile = path.join(DATA_DIR, 'responses.csv');
    
    if (!fs.existsSync(responsesFile)) {
      return res.json({ count: 0 });
    }
    
    const content = fs.readFileSync(responsesFile, 'utf8');
    const lines = content.split('\n');
    
    if (lines.length < 2) {
      return res.json({ count: 0 });
    }
    
    const uniqueQuestions = new Set();
    
    for (let i = 1; i < lines.length; i++) {
      const line = lines[i].trim();
      if (!line) continue;
      
      const participantMatch = line.match(/^([^,]+),/);
      if (participantMatch && participantMatch[1] === id) {
        
        let match = line.match(/questionId.*?([P][0-9A-Z]+)/);
        if (match) {
          uniqueQuestions.add(match[1]);
        }
      }
    }
    
    res.json({ count: uniqueQuestions.size });
    
  } catch (error) {
    console.error('Error counting unique responses:', error);
    res.json({ count: 0 });
  }
});

