app.get('/admin/clean-orphaned-responses', async (req, res) => {
  try {
    // Obtener participantes actuales del CSV
    const currentParticipants = Array.from(loadParticipants().keys());
    
    // Encontrar respuestas hu√©rfanas
    const orphanedResponses = questionnaireDb.prepare(`
      SELECT DISTINCT participant_id 
      FROM questionnaire_responses 
      WHERE participant_id NOT IN (${currentParticipants.map(() => '?').join(',')})
    `).all(...currentParticipants);
    
    const orphanedStatus = questionnaireDb.prepare(`
      SELECT DISTINCT participant_id 
      FROM questionnaire_status 
      WHERE participant_id NOT IN (${currentParticipants.map(() => '?').join(',')})
    `).all(...currentParticipants);
    
    // Eliminar registros hu√©rfanos
    let deletedResponses = 0;
    let deletedStatus = 0;
    
    for (const orphan of orphanedResponses) {
      const result = questionnaireDb.prepare(`
        DELETE FROM questionnaire_responses WHERE participant_id = ?
      `).run(orphan.participant_id);
      deletedResponses += result.changes;
    }
    
    for (const orphan of orphanedStatus) {
      const result = questionnaireDb.prepare(`
        DELETE FROM questionnaire_status WHERE participant_id = ?
      `).run(orphan.participant_id);
      deletedStatus += result.changes;
    }
    
    res.json({ 
      success: true, 
      orphanedParticipants: [...orphanedResponses, ...orphanedStatus].map(o => o.participant_id),
      deletedResponses,
      deletedStatus,
      currentParticipants
    });
    
  } catch (error) {
    res.json({ success: false, error: error.message });
  }
});




