app.get('/api/questionnaire/load/:id', (req, res) => {
  try {
    const participantId = String(req.params.id || '').trim();
    const participant = lookupParticipant(participantId);

    if (!participant) {
      return res.status(404).json({ ok: false, error: 'Participante no encontrado' });
    }

    // Leer respuestas del participante
    const rows = db.prepare(`
      SELECT question_id, answer, ts_utc
      FROM questionnaire_answers
      WHERE participant_id = ?
      ORDER BY question_id
    `).all(participantId);

// Parsear JSON si procede y devolver en formato correcto
    const answers = {};
    for (const r of rows) {
      let v = r.answer;
      if (typeof v === 'string' && (v.startsWith('[') || v.startsWith('{'))) {
        try { v = JSON.parse(v); } catch {}
      }
      // Devolver en formato {answer: "...", ts_utc: "..."}
      answers[r.question_id] = {
        answer: v,
        ts_utc: r.ts_utc
      };
    }
    // Estado
    const st = db.prepare(`
      SELECT participant_id, current_question, completed, completed_at
      FROM questionnaire_status
      WHERE participant_id = ?
    `).get(participantId);

    const status = st
      ? {
          participant_id: st.participant_id,
          current_question: st.current_question || null,
          completed: !!st.completed,
          completed_at: st.completed_at || null
        }
      : { current_question: 'intro_1', completed: false };

res.json({
      ok: true,
      participant: {
        id: participant.id,
        nombre: participant.full_name || participant.name || participantId,
        gender: participant.gender || ''
      },
      answers,
      status,
        canEdit: (participant.active !== 'no')
    });
  } catch (error) {
    console.error('Error loading questionnaire:', error);
    res.status(500).json({ ok: false, error: 'Error interno del servidor' });
  }







});




// Endpoint mejorado para audio sin multer - con manejo robusto de errores
app.post('/api/questionnaire/save-audio-simple', (req, res) => {
  console.log('=== ENDPOINT AUDIO SIMPLE ===');
  
  // Variables para control de estado
  let chunks = [];
  let totalSize = 0;
  const MAX_SIZE = 100 * 1024 * 1024; // 100MB l√≠mite
  let responseSent = false;
  let timeoutId = null;
  
  // Funci√≥n helper para enviar respuesta solo una vez
  const sendResponse = (statusCode, data) => {
    if (responseSent) {
      console.warn('Intento de enviar respuesta duplicada - ignorando');
      return;
    }
    responseSent = true;
    
    // Limpiar timeout si existe
    if (timeoutId) {
      clearTimeout(timeoutId);
      timeoutId = null;
    }
    
    // Limpiar listeners para evitar memory leaks
    req.removeAllListeners('data');
    req.removeAllListeners('end');
    req.removeAllListeners('error');
    req.removeAllListeners('close');
    
    res.status(statusCode).json(data);
  };
  
  // Timeout de 5 minutos para la operaci√≥n completa
  timeoutId = setTimeout(() => {
    console.error('Timeout en subida de audio');
    sendResponse(408, { ok: false, error: 'Timeout en la subida del audio' });
  }, 5 * 60 * 1000);
  
  // Validaci√≥n temprana de headers
  const participantId = req.headers['x-participant-id'];
  const questionId = req.headers['x-question-id'];
  
  if (!participantId || !questionId) {
    console.error('Headers faltantes:', { participantId, questionId });
    return sendResponse(400, { ok: false, error: 'Headers faltantes (x-participant-id, x-question-id)' });
  }
  
  // Validar Content-Type
  const contentType = req.headers['content-type'] || '';
  if (!contentType.includes('audio/') && !contentType.includes('application/octet-stream')) {
    console.warn('Content-Type inesperado:', contentType, '- continuando de todas formas');
  }
  
  // Manejar datos recibidos
  req.on('data', chunk => {
    if (responseSent) return; // Ignorar datos si ya se envi√≥ respuesta
    
    totalSize += chunk.length;
    
    // Verificar l√≠mite de tama√±o
    if (totalSize > MAX_SIZE) {
      console.error('Archivo demasiado grande:', totalSize);
      sendResponse(413, { ok: false, error: 'Archivo demasiado grande (m√°x 100MB)' });
      req.pause(); // Detener recepci√≥n de datos
      return;
    }
    
    chunks.push(chunk);
  });
  
  // Manejar finalizaci√≥n de la transferencia
  req.on('end', () => {
    if (responseSent) return; // Ya se envi√≥ respuesta (timeout o error)
    
    try {
      const audioBuffer = Buffer.concat(chunks);
      
      console.log('Audio recibido - participantId:', participantId, 'questionId:', questionId, 'size:', audioBuffer.length);
      
      // Validar que se recibieron datos
      if (audioBuffer.length === 0) {
        return sendResponse(400, { ok: false, error: 'No se recibi√≥ audio' });
      }
      
      // Validar tama√±o m√≠nimo (1KB)
      if (audioBuffer.length < 1024) {
        return sendResponse(400, { ok: false, error: 'Audio demasiado corto o corrupto' });
      }
      
      // Crear directorio si no existe
      const audioDir = path.join(DATA_DIR, 'audio');
      if (!fs.existsSync(audioDir)) {
        console.log('Creando directorio de audio:', audioDir);
        fs.mkdirSync(audioDir, { recursive: true });
      }
      
      // Generar nombre de archivo seguro
      const timestamp = Date.now();
      const sanitizedParticipantId = String(participantId).replace(/[^a-zA-Z0-9_-]/g, '_');
      const sanitizedQuestionId = String(questionId).replace(/[^a-zA-Z0-9_-]/g, '_');
      const filename = `${sanitizedParticipantId}_${sanitizedQuestionId}_${timestamp}.webm`;
      const filepath = path.join(audioDir, filename);
      
      // Guardar archivo de forma segura
      try {
        fs.writeFileSync(filepath, audioBuffer, { mode: 0o644 });
        console.log('Archivo guardado:', filepath);
      } catch (fsError) {
        console.error('Error escribiendo archivo:', fsError);
        return sendResponse(500, { ok: false, error: 'Error guardando archivo de audio' });
      }
      
      // Verificar que el archivo se guard√≥ correctamente
      if (!fs.existsSync(filepath)) {
        console.error('Archivo no se guard√≥ correctamente');
        return sendResponse(500, { ok: false, error: 'Error verificando archivo guardado' });
      }
      
      // Guardar en base de datos
      try {
        qaUpsert.run({
          participant_id: participantId,
          question_id: questionId + '_audio',
          answer: JSON.stringify({
            type: 'audio',
            filename: filename,
            size: audioBuffer.length,
            timestamp: new Date().toISOString()
          }),
          ts_utc: new Date().toISOString()
        });
        console.log('Registro guardado en BD para:', filename);
      } catch (dbError) {
        console.error('Error guardando en BD:', dbError);
        // Intentar eliminar el archivo si falla la BD
        try {
          fs.unlinkSync(filepath);
        } catch (unlinkError) {
          console.error('Error eliminando archivo hu√©rfano:', unlinkError);
        }
        return sendResponse(500, { ok: false, error: 'Error guardando en base de datos' });
      }
      
      console.log('Audio guardado exitosamente:', filename);
      sendResponse(200, { 
        ok: true, 
        audioData: { 
          filename,
          size: audioBuffer.length,
          timestamp: new Date().toISOString()
        } 
      });
      
      // Liberar memoria
      chunks = [];
      
    } catch (error) {
      console.error('Error procesando audio en evento end:', error);
      sendResponse(500, { ok: false, error: 'Error interno procesando audio' });
    }
  });
  
  // Manejar errores en el stream
  req.on('error', (error) => {
    if (responseSent) return;
    console.error('Error en stream de audio:', error);
    sendResponse(500, { ok: false, error: 'Error en la transferencia del audio' });
  });
  
  // Manejar desconexi√≥n del cliente
  req.on('close', () => {
    if (responseSent) return;
    console.warn('Cliente desconectado antes de completar la subida');
    
    // Limpiar recursos
    chunks = [];
    if (timeoutId) {
      clearTimeout(timeoutId);
      timeoutId = null;
    }
  });
});


// ----- API: Servir archivos de audio (mejorado) -----
app.get('/api/audio/:filename', (req, res) => {
  try {
    const filename = req.params.filename;
    
    // Validar nombre de archivo para prevenir path traversal
    if (!filename || filename.includes('..') || filename.includes('/') || filename.includes('\\')) {
      console.warn('Intento de acceso a archivo inv√°lido:', filename);
      return res.status(400).json({ error: 'Nombre de archivo inv√°lido' });
    }
    
    // Validar extensi√≥n
    if (!filename.endsWith('.webm')) {
      console.warn('Extensi√≥n de archivo no permitida:', filename);
      return res.status(400).json({ error: 'Tipo de archivo no permitido' });
    }
    
    const audioPath = path.join(DATA_DIR, 'audio', filename);
    
    // Verificar que el archivo existe
    if (!fs.existsSync(audioPath)) {
      console.warn('Archivo de audio no encontrado:', audioPath);
      return res.status(404).json({ error: 'Archivo de audio no encontrado' });
    }
    
    // Verificar que es un archivo (no un directorio)
    const stats = fs.statSync(audioPath);
    if (!stats.isFile()) {
      console.error('La ruta no es un archivo:', audioPath);
      return res.status(400).json({ error: 'Ruta inv√°lida' });
    }
    
    // Configurar headers apropiados
    res.setHeader('Content-Type', 'audio/webm');
    res.setHeader('Content-Length', stats.size);
    res.setHeader('Accept-Ranges', 'bytes');
    res.setHeader('Cache-Control', 'private, max-age=3600'); // Cache de 1 hora
    
    // Crear stream de lectura para mejor manejo de archivos grandes
    const readStream = fs.createReadStream(audioPath);
    
    // Manejar errores en el stream
    readStream.on('error', (error) => {
      console.error('Error leyendo archivo de audio:', error);
      if (!res.headersSent) {
        res.status(500).json({ error: 'Error leyendo archivo de audio' });
      }
    });
    
    // Enviar el archivo
    readStream.pipe(res);
    
    console.log('Sirviendo archivo de audio:', filename, 'tama√±o:', stats.size);
    
  } catch (error) {
    console.error('Error sirviendo audio:', error);
    if (!res.headersSent) {
      res.status(500).json({ error: 'Error interno del servidor' });
    }
  }
});



