// Funci√≥n para limpiar respuestas de preguntas que ya no deben mostrarse
function cleanupConditionalAnswers(changedQuestionId) {
  console.log('üßπ Limpiando respuestas condicionadas despu√©s de cambiar', changedQuestionId);
  
  // Definir qu√© preguntas dependen de cu√°les (mapa de dependencias)
const dependencies = {
  'P01': ['P02A'],    // ‚úÖ Cuando P01 cambia, revisar P02A
  'P06': ['P07'],     // ‚úÖ Cuando P06 cambia, revisar P07  
  'P22a': ['P22b'],   // ‚úÖ Cuando P22a cambia, revisar P22b
};
  
  // Si la pregunta cambiada tiene dependencias
  if (dependencies[changedQuestionId]) {
    dependencies[changedQuestionId].forEach(dependentQuestionId => {
      // Buscar la pregunta dependiente en la estructura
      const dependentQuestion = state.structure.questions.find(q => q.id === dependentQuestionId);
      
      if (dependentQuestion) {
        // Evaluar si la pregunta dependiente deber√≠a mostrarse ahora
        const shouldShow = shouldShowQuestion(dependentQuestion);
        
        // Si NO deber√≠a mostrarse pero tiene respuesta guardada, limpiarla
        if (!shouldShow && state.answers[dependentQuestionId]) {
          console.log('üóëÔ∏è Eliminando respuesta de', dependentQuestionId, 'porque ya no aplica');
          console.log('üóëÔ∏è Respuesta anterior era:', state.answers[dependentQuestionId]);
          
          // Limpiar del estado local
          delete state.answers[dependentQuestionId];
          
          // Tambi√©n limpiar del servidor - M√öLTIPLES LLAMADAS para asegurar limpieza
          saveAnswer(dependentQuestionId, null);           // Eliminar valor √∫nico
          saveAnswer(dependentQuestionId, []);             // Eliminar array vac√≠o  
          saveAnswer(dependentQuestionId, undefined);       // Eliminar undefined
          
          console.log('‚úÖ Estado limpiado para', dependentQuestionId);
        }
      }
    });
  }
}

function handlePhaseRadioChange(event) {
  // Usar la funci√≥n existente que ya maneja el guardado
  handleRadioChange(event);
  
  const questionId = event.target.name;
  
  // Si se respondi√≥ una de las preguntas de fase, re-renderizar para actualizar la pregunta final
  if (questionId.startsWith('P09_phase')) {
    // Memorizar posici√≥n exacta del cursor
    const scrollPos = window.pageYOffset;
    console.log('üî• Memorizando posici√≥n:', scrollPos);
    
    // Timeout m√°s largo para asegurar que el estado se guarde
    setTimeout(() => {
      console.log('üî• Ejecutando renderCurrentQuestion...');
      renderCurrentQuestion();
      
      // Timeout m√°s largo para el scroll de vuelta
      setTimeout(() => {
        console.log('üî• Restaurando posici√≥n a:', scrollPos);
        
        // Scroll m√°s robusto con requestAnimationFrame
        requestAnimationFrame(() => {
          window.scrollTo(0, scrollPos);
          console.log('üî• Scroll ejecutado, posici√≥n actual:', window.pageYOffset);
        });
        
      }, 200); // Aumentado de 10ms a 200ms
      
}, 100); // Aumentado de 50ms a 100ms
  }
  
  // ‚úÖ A√ëADIR AL FINAL
  setTimeout(() => {
    updateNavigationButtons();
  }, 100);
}

