function goToLastAnswered() {
  let lastAnsweredQuestionId = null;
  
  // Buscar en preguntas filtradas (excluyendo intro y completion)
  const questionIds = state.allQuestions.filter(qId => 
    !qId.startsWith('intro_') && qId !== 'completion'
  );
  
  // Encontrar la √∫ltima respondida
  for (let i = questionIds.length - 1; i >= 0; i--) {
    const qId = questionIds[i];
    const answer = state.answers[qId];
    
    if (answer && answer.answer && String(answer.answer).trim() !== '') {
      lastAnsweredQuestionId = qId;
      break;
    }
  }
  
  if (lastAnsweredQuestionId) {
    state.currentQuestion = lastAnsweredQuestionId;
    renderCurrentQuestion();
  }
}



