function setRanking(questionId, optionValue, rank) {
  if (!state.canEdit) return;
  
  // Inicializar estructura limpia
  if (!state.answers[questionId]) {
    state.answers[questionId] = { answer: {}, ts_utc: new Date().toISOString() };
  }
  
  let ranking = state.answers[questionId].answer;
  
  // Asegurar que ranking es un objeto limpio
  if (typeof ranking === 'string') {
    try {
      ranking = JSON.parse(ranking);
    } catch (e) {
      ranking = {};
    }
  }
  
  if (!ranking || typeof ranking !== 'object') {
    ranking = {};
  }
  
  // Si ya hay otra opci√≥n con este ranking, quit√°rselo
  for (const [key, value] of Object.entries(ranking)) {
    if (value == rank && key !== optionValue) {
      delete ranking[key];
    }
  }
  
  // Asignar o quitar el ranking
  if (ranking[optionValue] == rank) {
    delete ranking[optionValue];
  } else {
    ranking[optionValue] = rank;
  }
  
  // Guardar limpiamente
  state.answers[questionId].answer = ranking;
  state.answers[questionId].ts_utc = new Date().toISOString();
  
  console.log('Ranking guardado:', questionId, '=', ranking);
  
  // Enviar objeto directamente
  saveAnswer(questionId, ranking);
  
  rerenderPreservingScroll(renderCurrentQuestion);
}  

async function saveAnswer(questionId, answer) {
  if (!state.canEdit) return;
  
  try {
    state.answers[questionId] = { answer: answer, ts_utc: new Date().toISOString() };
    const response = await fetch('/api/questionnaire/save', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        participantId: state.participantId,
        questionId: questionId,
        answer: answer,
        currentQuestion: state.currentQuestion
      })
    });
    const data = await response.json();
    if (!data.ok) {
      console.error('Error guardando respuesta:', data.error);
    } else {
      console.log('Respuesta guardada:', questionId, '=', answer);
    }
  } catch (error) {
    console.error('Error guardando:', error);
  }
}


