function calculateTotalQuestionsForParticipant() {
  let totalQuestions = 0;
  
  state.structure.questions.forEach(question => {
    if (shouldShowQuestion(question)) {
      // üÜï ESPECIAL: P09 cuenta como 4 preguntas (sus sub-preguntas)
      if (question.id === 'P09' && question.type === 'phases' && question.questions && Array.isArray(question.questions)) {
        totalQuestions += question.questions.length; // A√±ade 4
      } else {
        totalQuestions++; // Preguntas normales cuentan como 1
      }
    }
  });
  
  console.log('üìä Total preguntas aplicables para este participante:', totalQuestions);
  return totalQuestions;
}


function calculateAnsweredQuestionsForParticipant() {
  let answeredCount = 0;
  
  state.structure.questions.forEach(question => {
    // Solo contar si la pregunta se mostrar√≠a
    if (shouldShowQuestion(question)) {
      
      // üÜï ESPECIAL: P09 - verificar las 4 sub-preguntas individualmente
      if (question.id === 'P09' && question.type === 'phases' && question.questions && Array.isArray(question.questions)) {
        question.questions.forEach(subQ => {
          if (state.answers[subQ.id]?.answer) {
            answeredCount++;
          }
        });


      } else if (question.type === 'ranking') {
        // üÜï ESPECIAL: Preguntas de ranking - verificar que todos los elementos est√©n ordenados
        const answer = state.answers[question.id]?.answer;
        if (answer && typeof answer === 'object') {
          const assignedRanks = Object.values(answer);
          const maxRank = question.maxRank || question.options.length;
          
          // Verificar que tenga exactamente maxRank elementos asignados
          // y que sean n√∫meros consecutivos del 1 al maxRank
          const hasAllRanks = assignedRanks.length === maxRank;
          const hasValidRanks = assignedRanks.every(rank => rank >= 1 && rank <= maxRank);
          const hasUniqueRanks = new Set(assignedRanks).size === assignedRanks.length;
          
          console.log('üî¢ Verificando ranking ' + question.id + ':', {
            assignedRanks: assignedRanks,
            maxRank: maxRank,
            hasAllRanks: hasAllRanks,
            hasValidRanks: hasValidRanks,
            hasUniqueRanks: hasUniqueRanks,
            isComplete: hasAllRanks && hasValidRanks && hasUniqueRanks
          });
          
          if (hasAllRanks && hasValidRanks && hasUniqueRanks) {
            answeredCount++;
          }
        }


      } else {
        // Preguntas normales
        if (state.answers[question.id]?.answer) {
          answeredCount++;
        }
      }
    }
  });
  
  console.log('‚úÖ Preguntas respondidas aplicables:', answeredCount);
  return answeredCount;
}


