function isQuestionAnswered(questionId) {
  const answer = state.answers[questionId] ? state.answers[questionId].answer : null;
  if (!answer) return false;
  
  // L√ìGICA ESPECIAL PARA PREGUNTAS DE RANKING
  const rankingQuestions = ['P11', 'P20', 'P21', 'P22b'];
  if (rankingQuestions.includes(questionId)) {
    if (typeof answer === 'object') {
      const assignedRanks = Object.values(answer);
      
      // Obtener maxRank seg√∫n la pregunta
      let maxRank;
      if (questionId === 'P22b') {
        maxRank = 4;
      } else {
        maxRank = 6; // P11, P20, P21
      }
      
      // Verificar que est√© completo
      const hasAllRanks = assignedRanks.length === maxRank;
      const hasValidRanks = assignedRanks.every(rank => rank >= 1 && rank <= maxRank);
      const hasUniqueRanks = new Set(assignedRanks).size === assignedRanks.length;
      
      return hasAllRanks && hasValidRanks && hasUniqueRanks;
    }
    return false;
  }
  
  // L√ìGICA NORMAL PARA OTRAS PREGUNTAS
  if (Array.isArray(answer)) return answer.length > 0;
  if (typeof answer === 'string') return answer.trim() !== '';
  if (questionId === 'P26') {
    const hasText = answer && String(answer).trim() !== '';
    return hasText;
  }
  return true;
}

