let currentAudioButton = null;

function playQuestionAudio(questionId) {
  if (!hasExplanatoryAudio(questionId)) {
    alert('No hay audio disponible para esta pregunta.');
    return;
  }
  
  // Buscar el bot√≥n espec√≠fico
  const button = document.getElementById('audioBtn_' + questionId) || 
                 document.querySelector('[onclick*="' + questionId + '"]');
  
  // Si ya hay audio reproduci√©ndose para esta pregunta ‚Üí pausa/resume
  if (currentAudio && currentAudioButton === button) {
  if (!currentAudio.paused) {
    currentAudio.pause();        // ‚è∏Ô∏è Pausa (mantiene posici√≥n)
    setButtonPaused(button);     // üü† Bot√≥n naranja
  } else {
    currentAudio.play();         // ‚ñ∂Ô∏è Resume
    setButtonPlaying(button);    // üü¢ Bot√≥n verde
  }
  return;
}
  
  // Si hay otro audio reproduci√©ndose, pararlo primero
  if (currentAudio) {
    stopCurrentAudio();
  }
  
  // Crear nuevo audio
const lang = state.language || 'es';
const spanishLanguages = ['es', 'ca', 'eu', 'gl', 'va'];
const langSuffix = spanishLanguages.includes(lang) ? '_es' : '_en';
const audioUrl = '/assets/audio/help/' + questionId + langSuffix + '.mp3';
const audio = new Audio(audioUrl);
  
  // Configurar eventos del audio
  audio.onerror = function() {
    alert('Error: No se pudo cargar el audio para esta pregunta.');
    resetButton(button);
  };
  
  audio.onended = function() {
    // Cuando termina naturalmente, resetear bot√≥n
    resetButton(button);
    currentAudio = null;
    currentAudioButton = null;
  };
  
  // Cambiar bot√≥n a estado "reproduciendo"
  if (button) {
    setButtonPlaying(button);
    currentAudioButton = button;
  }
  
  // Reproducir audio
  audio.play().then(() => {
    currentAudio = audio;
  }).catch(function(error) {
    console.error('Error reproduciendo audio:', error);
    alert('Error al reproducir el audio. Verifique que el archivo existe.');
    resetButton(button);
  });
}

