    function esc(s){
      if(s==null) return '';
      return String(s).replace(/&/g,'&amp;').replace(/</g,'&lt;').replace(/>/g,'&gt;').replace(/\"/g,'&quot;').replace(/'/g,'&#39;');
    }

    function parseISO(s){
      if(!s) return null; var d=new Date(s); return isNaN(d.getTime())?null:d;
    }
    
    function lastActivity(rows){
      var latest = new Date(0);
      rows.forEach(function(x){
        var a = parseISO(x.answer_ts_utc);
        if(a && a > latest) latest = a;
        var c = parseISO(x.completed_at);
        if(c && c > latest) latest = c;
      });
      return latest.getTime()===0 ? null : latest;
    }
    
    function formatWhen(d){
      if(!d) return 'Nunca';
      var now=new Date(), diff=now-d, min=Math.floor(diff/60000), h=Math.floor(diff/3600000), day=Math.floor(diff/86400000);
      if(min<60) return 'Hace ' + min + ' min';
      if(h<24) return 'Hace ' + h + 'h';
      if(day<7) return 'Hace ' + day + ' d√≠as';
      try { return d.toLocaleDateString('es-ES'); } catch(_) { return d.toISOString().slice(0,10); }
    }

    function compute(rows){
      // Agrupa por participant_id
      var byId = {};
      rows.forEach(function(r){
        var id = r.participant_id; if(!id) return;
        (byId[id] = byId[id] || []).push(r);
      });
      
      // Construye resumen
      var list = [];
      Object.keys(byId).forEach(function(id){
        var rs = byId[id];
        var completed = rs.some(function(x){ return Number(x.completed)===1; });
var questionsAnswered = rs.filter(function(x){
  return x.question_id && x.question_id.charAt(0)==='P' && x.question_id.indexOf('_')===-1;
}).length;
var totalQ = 26;
var progress = completed ? 100 : Math.min(95, Math.max(0, Math.round((questionsAnswered/totalQ)*100)));
var last = lastActivity(rs);

// üÜï L√ìGICA CORREGIDA: Solo "En Progreso" si ha respondido al menos 1 pregunta
var status = completed ? 'completed' : 
             (questionsAnswered > 0 ? 'inProgress' : 'notStarted');





        
        // Buscar nombre del participante
        var name = id; // Por defecto usar el ID
        // Aqu√≠ podr√≠as hacer una lookup del CSV si tienes acceso a esos datos


        // DEBUG: Ver por qu√© se clasifica cada participante
console.log('Clasificando ' + id + ':', {
  completed: completed,
  questionsAnswered: questionsAnswered,
  totalRows: rs.length,
  status: status
});
        
        list.push({ id, name, status, progress, last });
      });
      
      return list;
    }

