app.post('/api/questionnaire/save', (req, res) => {

  // üîç DEBUG TEMPORAL
  // console.log('=== SAVE ENDPOINT ===');
  console.log('Content-Type:', req.headers['content-type']);
  console.log('Content-Length:', req.headers['content-length']);
  console.log('Body:', req.body);
  console.log('Body type:', typeof req.body);
  console.log('=============================');


  try {
    const { participantId, questionId, answer, currentQuestion } = req.body;
    
    if (!participantId || !questionId) {
      return res.status(400).json({ ok: false, error: 'Datos incompletos' });
    }

    // üÜï MEJORADO: Manejo de eliminaci√≥n m√°s robusto
    if (answer === null || answer === undefined || 
        (Array.isArray(answer) && answer.length === 0) ||
        answer === '' || answer === 'null' || answer === 'undefined') {
      
      console.log('üóëÔ∏è Eliminando respuesta del servidor para', questionId, '- valor recibido:', answer);
      
      // Usar DELETE para limpiar completamente
      const deleteStmt = db.prepare('DELETE FROM questionnaire_answers WHERE participant_id = ? AND question_id = ?');
      deleteStmt.run(participantId, String(questionId));
      
      console.log('‚úÖ Respuesta eliminada del servidor para', questionId);
      
    } else {
      // Comportamiento normal: guardar/actualizar
      qaUpsert.run({
        participant_id: participantId,
        question_id: String(questionId),
        answer: (answer == null ? '' : (typeof answer === 'object' ? JSON.stringify(answer) : String(answer))),
        ts_utc: new Date().toISOString()
      });
    }

    // El resto sigue igual
    const existing = qsGet.get(participantId);
    const payload = {
      participant_id: participantId,
      current_question: currentQuestion || null,
      completed: 0,
      completed_at: null
    };
    
    if (existing) qsUpdate.run(payload);
    else qsInsert.run(payload);

    res.json({ ok: true });
  } catch (error) {
    console.error('Error saving/deleting answer:', error);
    res.status(500).json({ ok: false, error: 'Error interno del servidor' });
  }
});


