app.post('/admin/questionnaire/reset/:id', (req, res) => {
  try {
    const participantId = req.params.id;
    
    if (!participantId) {
      return res.status(400).json({ 
        ok: false, 
        error: 'ID de participante requerido' 
      });
    }

    // CORREGIDO: Usar db y questionnaire_answers (seg√∫n el debug de tables)
    const deletedResponses = db.prepare('DELETE FROM questionnaire_answers WHERE participant_id = ?').run(participantId);
    const deletedStatus = db.prepare('DELETE FROM questionnaire_status WHERE participant_id = ?').run(participantId);

    // A√ëADIR: Limpiar participante del archivo CSV
    const responsesFile = path.join(DATA_DIR, 'responses.csv');
    let csvLinesRemoved = 0;
    if (fs.existsSync(responsesFile)) {
      const content = fs.readFileSync(responsesFile, 'utf8');
      const lines = content.split('\n');
      
      // Filtrar l√≠neas que no pertenezcan a este participante
      const filteredLines = lines.filter((line, index) => {
        if (index === 0) return true; // Mantener header
        if (!line.trim()) return false; // Eliminar l√≠neas vac√≠as
        
        const columns = line.split(',');
        const lineParticipantId = columns[0]?.trim().replace(/"/g, '');
        return lineParticipantId !== participantId;
      });
      
      csvLinesRemoved = lines.length - filteredLines.length;
      fs.writeFileSync(responsesFile, filteredLines.join('\n'), 'utf8');
    }

    const totalDeleted = deletedResponses.changes + deletedStatus.changes;

    console.log(`[ADMIN RESET] Participante ${participantId}: eliminadas ${deletedResponses.changes} respuestas, ${deletedStatus.changes} estados, ${csvLinesRemoved} l√≠neas CSV`);

    res.json({ 
      ok: true, 
      message: `Participante ${participantId} reseteado correctamente`,
      deleted: totalDeleted
    });
  } catch (error) {
    console.error('[reset participant] error:', error);
    res.status(500).json({ 
      ok: false, 
      error: 'Error reseteando participante: ' + error.message 
    });
  }
});






