// ===== Participantes CSV (robusto) =====
function stripBOM(s){ return s ? s.replace(/^\uFEFF/, '') : s; }
function detectDelimiter(headerLine){
  let inQ=false, commas=0, semis=0;
  for (let i=0;i<headerLine.length;i++){
    const ch = headerLine[i];
    if (ch === '"'){ if (inQ && headerLine[i+1] === '"') { i++; } else { inQ=!inQ; } }
    else if (!inQ){ if (ch === ',') commas++; else if (ch === ';') semis++; }
  }
  if (semis > 0 && commas === 0) return ';';
  if (commas > 0) return ',';
  return ',';
}
function cleanupCsvLine(line){
  let s = stripBOM(line);
  if (s.startsWith('"') && s.endsWith('"')) s = s.slice(1, -1);
  s = s.replace(/"+$/, '');
  return s;
}
function parseRow(line, delim){
  const out = []; let cur = '', inQ=false;
  for (let i=0;i<line.length;i++){
    const ch = line[i];
    if (ch === '"'){ if (inQ && line[i+1] === '"'){ cur+='"'; i++; } else { inQ=!inQ; } }
    else if (ch === delim && !inQ){ out.push(cur); cur=''; }
    else { cur += ch; }
  }
  out.push(cur);
  return out;
}
function readParticipantsFile(){
  try {
    if (!fs.existsSync(PARTICIPANT_CSV)) {
      console.warn('[participants CSV] no existe:', PARTICIPANT_CSV);
      return { map:new Map(), mtimeMs:0 };
    }
    const stat = fs.statSync(PARTICIPANT_CSV);
    const rawContent = fs.readFileSync(PARTICIPANT_CSV);
    const content = rawContent.toString('utf8');




    const raw = content.split(/\r?\n/).filter(l => l.trim() !== '');
    if (raw.length === 0) return { map:new Map(), mtimeMs:stat.mtimeMs };

    const headerClean = cleanupCsvLine(raw[0]);
    const delim = detectDelimiter(headerClean);
    const headers = parseRow(headerClean, delim).map(h => stripBOM(h).trim().toLowerCase());

    const idx = (name, fb) => { const p = headers.indexOf(name); return p >= 0 ? p : fb; };
    const idIdx        = idx('id',        0);
    const nameIdx      = idx('name',      1);
    const fullNameIdx  = idx('full_name', 2);
    const emailIdx     = idx('email',     3);
    const langIdx      = idx('lang',      4);
    const carrierIdx   = idx('carrier',   5);
    const dniIdx       = headers.indexOf('dni');

    let genderIdx = ['gender','genero','sexo'].map(h => headers.indexOf(h)).find(i => i >= 0);

    let passwordIdx = headers.indexOf('password');

    let authIdx = headers.indexOf('require_auth');

    let activeIdx = headers.indexOf('active');

    // console.log CSV - headers completos:', headers);
// console.log CSV - activeIdx encontrado:', activeIdx);
// console.log CSV - delim detectado:', JSON.stringify(delim));


    if ((genderIdx == null || genderIdx < 0) && dniIdx >= 0 && (dniIdx + 1) < headers.length) {
      genderIdx = dniIdx + 1;
    }

    const m = new Map();
    for (let i = 1; i < raw.length; i++) {
      const line = cleanupCsvLine(raw[i]); if (!line) continue;
      const cols = parseRow(line, delim).map(c => stripBOM(c).trim());
      const id = (cols[idIdx] || '').trim(); if (!id) continue;

      const genderRaw = (genderIdx >= 0 && genderIdx < cols.length) ? cols[genderIdx] : '';
      const passwordRaw = (passwordIdx >= 0 && passwordIdx < cols.length) ? cols[passwordIdx] : '';
      const authRaw = (authIdx >= 0 && authIdx < cols.length) ? cols[authIdx] : '';
      const activeRaw = (activeIdx >= 0 && activeIdx < cols.length) ? cols[activeIdx] : 'yes';

      m.set(id, {
  id,
  name: (cols[nameIdx] || '').trim(),
  full_name: (cols[fullNameIdx] || '').trim(),
  email: (cols[emailIdx] || '').trim(),
  lang: (cols[langIdx] || '').trim().toLowerCase() || 'es',
  carrier: ((cols[carrierIdx] || '').trim() || '').toLowerCase(),
  dni: dniIdx >= 0 ? (cols[dniIdx] || '').trim() : '',
  gender: normalizeGender(genderRaw),
  password: passwordRaw.trim(),
  require_auth: authRaw.trim(),
  active: activeRaw.trim() || 'yes'
});
    }
    console.log(`[participants CSV] cargados ${m.size} registros (delim="${delim}")`);
    return { map:m, mtimeMs:stat.mtimeMs };
  } catch (e) {
    console.error('[participants CSV] error leyendo archivo:', e);
    return { map:new Map(), mtimeMs:0 };
  }
}
let participantsCache = { map:new Map(), mtimeMs:-1 };
function loadParticipants(){
  try {
    const stat = fs.existsSync(PARTICIPANT_CSV) ? fs.statSync(PARTICIPANT_CSV) : null;
    if (!stat) { participantsCache = { map:new Map(), mtimeMs:-1 }; return participantsCache.map; }
    if (stat.mtimeMs !== participantsCache.mtimeMs) { participantsCache = readParticipantsFile(); }
    return participantsCache.map;
  } catch (e) { console.error('[participants CSV] recarga:', e); return new Map(); }
}
function lookupParticipant(id){
  const q = String(id || '').trim();
  const map = loadParticipants();
  const hit = map.get(q) || null;
  if (!hit) console.warn('[whoami] id no encontrado en CSV:', q);
  return hit;
}


