// Funci√≥n para verificar si requiere autenticaci√≥n
function requiresAuth(participantId) {
  const participant = lookupParticipant(participantId);
  if (!participant) return false;
  
  return participant.require_auth && 
         participant.require_auth.toLowerCase() === 'yes';
}

// Funci√≥n para verificar credenciales
function verifyCredentials(email, password, participantId) {
  const participant = lookupParticipant(participantId);
  if (!participant) return false;
  
  return participant.email && 
         participant.password && 
         participant.email.toLowerCase() === email.toLowerCase() &&
         participant.password === password;
}

