// ===== Normalizaci√≥n & validaci√≥n =====
function normalizeResponse(v){
  if (!v) return null;
  const s = String(v).trim().toLowerCase();
  const noAccents = s.normalize('NFD').replace(/[\u0300-\u036f]/g, '');
  const key = noAccents.replace(/\s+/g, '_').replace(/-+/g, '_');
  if (['acepto','si','s√≠','accept','accepted','acepta','accepto'].includes(key)) return 'acepto';
  if (['necesito_mas_info','necesito_mas_informacion','mas_info','need_more_info','more_info','info','mas','informacion','necesito_info'].includes(key)) return 'necesito_mas_info';
  if (['rechazo','no','declino','declinar','reject','rejected','rechazar','rechaza'].includes(key)) return 'rechazo';
  return null;
}
const VALID_RESPONSES = new Set(['acepto','necesito_mas_info','rechazo']);
const idRegex = /^[A-Za-z0-9_\-:.]{3,128}$/;
function validatePayload({ response, id, token }) {
  if (!response || !VALID_RESPONSES.has(response)) return 'Par√°metro "response" inv√°lido (use: acepto | necesito_mas_info | rechazo).';
  if (!id || !idRegex.test(id)) return 'Par√°metro "id" inv√°lido (alfanum√©rico, _ - : . , 3‚Äì128 chars).';
  if (SUBMISSION_TOKEN && token !== SUBMISSION_TOKEN) return 'Token de env√≠o inv√°lido.';
  return null;
}

